import React, { useState } from "react";
import GalleryHero from "../components/GalleryHero";
import GalleryFilters from "../components/GalleryFilters";
import GalleryGrid from "../components/GalleryGrid";

function GalleryPage() {
  const categories = [
    "All",
    "Location",
    "Interior",
    "Rooms",
    "Restaurant",
    "Amenities",
  ];

  const allImages = [
    {
      url: "https://res.cloudinary.com/demo/image/upload/sample.jpg",
      category: "Location",
    },
    {
      url: "https://res.cloudinary.com/demo/image/upload/v1690975673/hotel_room.jpg",
      category: "Rooms",
    },
    {
      url: "https://res.cloudinary.com/demo/image/upload/v1690975673/restaurant.jpg",
      category: "Restaurant",
    },
    {
      url: "https://res.cloudinary.com/demo/image/upload/v1690975673/lobby.jpg",
      category: "Interior",
    },
  ];

  const [filtered, setFiltered] = useState(allImages);

  const handleFilterChange = (category) => {
    if (category === "All") {
      setFiltered(allImages);
    } else {
      setFiltered(allImages.filter((img) => img.category === category));
    }
  };

  return (
    <>
      <GalleryHero />
      <GalleryFilters
        categories={categories}
        onFilterChange={handleFilterChange}
      />
      <GalleryGrid images={filtered} />
    </>
  );
}

export default GalleryPage;
