import React from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "./css/RoomSection.css";

function RoomsSection() {
  const rooms = [
    {
      image:
        "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822641/deluxeRoom_ysyd2x.jpg",
      title: "Deluxe Room",
      desc: "Comfortable room with modern amenities",
      price: "₹2,999/night",
      rating: 5,
    },
    {
      image:
        "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822642/premiumRoom_uupmcg.jpg",
      title: "Premium Suite",
      desc: "Spacious suite with Ganges view",
      price: "₹4,999/night",
      rating: 5,
    },
    {
      image:
        "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822642/traditionalRoom_sdbglh.jpg",
      title: "Traditional Room",
      desc: "Traditional decor with modern comfort",
      price: "₹3,499/night",
      rating: 5,
    },
  ];

  return (
    <div className="py-5" style={{ backgroundColor: "#fff" }}>
      <Container>
        <div className="text-center mb-4">
          <h3 className="fw-bold">Our Rooms</h3>
          <p className="text-muted">
            Choose from our selection of comfortable and elegant rooms
          </p>
        </div>

        <Row className="justify-content-center">
          {rooms.map((room, index) => (
            <Col
              key={index}
              md={4}
              className="mb-4 d-flex justify-content-center"
            >
              <Card className="room-card border-0 shadow-sm rounded-4">
                <div className="position-relative">
                  <Card.Img
                    variant="top"
                    src={room.image}
                    alt={room.title}
                    className="room-image rounded-top-4"
                    loading="lazy"
                  />
                  <div className="price-badge position-absolute top-0 end-0 m-3 px-3 py-1 rounded-3">
                    {room.price}
                  </div>
                </div>
                <Card.Body className="text-center">
                  <Card.Title className="fw-semibold">{room.title}</Card.Title>
                  <Card.Text className="text-muted mb-2">{room.desc}</Card.Text>
                  <div className="text-warning mb-3">
                    {Array.from({ length: room.rating }).map((_, i) => (
                      <i key={i} className="fa-solid fa-star"></i>
                    ))}
                  </div>
                  <Button
                    className="w-100 rounded-4"
                    style={{
                      backgroundColor: "#F54900",
                      fontWeight: "500",
                    }}
                  >
                    Book Now
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
}

export default RoomsSection;
