import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Tabs,
  Tab,
  Form,
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    type: "",
    guests: "",
    size: "",
    price: "",
    amenities: [],
    description: "",
    status: "Available",
    current_guest: "",
  });

  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5050/api/rooms")
      .then((res) => res.json())
      .then((data) => {
        console.log("Rooms API response:", data);
        setRooms(Array.isArray(data.data) ? data.data : []);
      })
      .catch((err) => console.error("Error fetching rooms:", err));
  }, []);

  useEffect(() => {
    toast.success("Login Successful", {
      position: "bottom-right",
      autoClose: 3000,
      theme: "light",
    });
  }, []);

  // üß† Handle logout
  const handleLogout = () => {
    toast.info("Logged out successfully", {
      position: "bottom-right",
      autoClose: 2000,
      theme: "light",
    });
    setTimeout(() => navigate("/admin"), 1000);
  };

  // üß† Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // üß† Handle checkbox (amenities)
  const handleAmenityChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => {
      if (checked) return { ...prev, amenities: [...prev.amenities, value] };
      else
        return {
          ...prev,
          amenities: prev.amenities.filter((a) => a !== value),
        };
    });
  };

  // üß† Submit form to backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5050/api/rooms/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await res.json();
      if (data.success) {
        toast.success("Room added successfully!", {
          position: "bottom-right",
          autoClose: 2000,
        });
        setFormData({
          name: "",
          type: "",
          guests: "",
          size: "",
          price: "",
          amenities: [],
          description: "",
        });
      } else {
        toast.error("‚ùå Failed to add room");
      }
    } catch (err) {
      console.error("Error:", err);
      toast.error("‚ö†Ô∏è Server error, please try again");
    }
  };

  return (
    <div className="admin-dashboard bg-light min-vh-100 py-4">
      <Container>
        {/* Header */}
        <div className="d-flex justify-content-between align-items-center mb-4">
          <div>
            <h3 className="fw-bold text-orange mb-1">Admin Panel</h3>
            <p className="text-muted small mb-0">Maheshwari Nivas Management</p>
          </div>

          <Button
            variant="outline-danger"
            className="rounded-3 px-3 fw-semibold"
            onClick={handleLogout}
          >
            <i className="bi bi-box-arrow-right me-2"></i> Logout
          </Button>
        </div>

        <hr />

        {/* Refresh Button */}
        <div className="d-flex justify-content-end align-items-center mb-4">
          <Button
            variant="outline-secondary"
            className="rounded-3 px-3 fw-semibold"
          >
            <i className="bi bi-arrow-clockwise me-2"></i> Refresh
          </Button>
        </div>

        {/* Stats Section */}
        <Row className="g-4 mb-4">
          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-primary-subtle text-primary">
                  <i className="bi bi-calendar3"></i>
                </div>
                <div className="ms-3">
                  <h6>Total Bookings</h6>
                  <h5 className="fw-bold">0</h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-warning-subtle text-warning">
                  <i className="bi bi-house-door"></i>
                </div>
                <div className="ms-3">
                  <h6>Today's Bookings</h6>
                  <h5 className="fw-bold">0</h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-success-subtle text-success">
                  <i className="bi bi-person-check"></i>
                </div>
                <div className="ms-3">
                  <h6>Confirmed</h6>
                  <h5 className="fw-bold">0</h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-purple-subtle text-purple">
                  <i className="bi bi-currency-dollar"></i>
                </div>
                <div className="ms-3">
                  <h6>Total Revenue</h6>
                  <h5 className="fw-bold">‚Çπ0.0K</h5>
                </div>
              </div>
            </Card>
          </Col>
        </Row>

        {/* Tabs Section */}
        <Tabs
          defaultActiveKey="bookings"
          id="admin-tabs"
          className="mb-3 equal-tabs rounded-pill bg-white shadow-sm p-2"
        >
          {/* Bookings Tab */}
          <Tab eventKey="bookings" title="Bookings">
            <Card
              className="p-5 text-center border-0 shadow-sm rounded-4 bg-white"
              style={{ height: "300px" }}
            >
              <i className="bi bi-calendar2 fs-1 mb-3 text-secondary"></i>
              <p className="text-muted mb-0">No bookings yet</p>
            </Card>
          </Tab>

          {/* üè® Rooms Status Tab */}
          <Tab eventKey="rooms" title="Rooms Status">
            <Card className="p-4 border-0 shadow-sm rounded-4 bg-white">
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h5 className="fw-bold mb-0">Room Status</h5>
                <Button
                  variant="outline-secondary"
                  onClick={() => window.location.reload()}
                  className="fw-semibold"
                >
                  <i className="bi bi-arrow-clockwise me-2"></i> Refresh
                </Button>
              </div>

              {rooms.length === 0 ? (
                <p className="text-center text-muted">No rooms found</p>
              ) : (
                <div className="table-responsive">
                  <table className="table align-middle table-hover">
                    <thead className="table-light">
                      <tr>
                        <th>Image</th>
                        <th>Room Number</th>
                        <th>Room Type</th>
                        <th>Status</th>
                        <th>Current Guest</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {rooms.map((room, index) => (
                        <tr key={index}>
                          <td>
                            {room.image ? (
                              <img
                                src={`http://localhost:5050/${room.image}`}
                                alt="Room"
                                style={{
                                  width: "60px",
                                  height: "60px",
                                  objectFit: "cover",
                                  borderRadius: "8px",
                                }}
                              />
                            ) : (
                              <div
                                style={{
                                  width: "60px",
                                  height: "60px",
                                  backgroundColor: "#f1f1f1",
                                  borderRadius: "8px",
                                  display: "flex",
                                  justifyContent: "center",
                                  alignItems: "center",
                                  color: "#888",
                                }}
                              >
                                <i className="bi bi-image"></i>
                              </div>
                            )}
                          </td>
                          <td>{room.name}</td>
                          <td>{room.type}</td>
                          <td>
                            <span
                              className={`badge ${
                                room.status === "Available"
                                  ? "bg-success-subtle text-success"
                                  : room.status === "Occupied"
                                  ? "bg-primary-subtle text-primary"
                                  : "bg-warning-subtle text-warning"
                              }`}
                            >
                              {room.status}
                            </span>
                          </td>
                          <td>{room.current_guest || "-"}</td>
                          <td>
                            <div className="d-flex gap-2">
                              <Button variant="outline-primary" size="sm">
                                <i className="bi bi-pencil"></i>
                              </Button>
                              <Button variant="outline-danger" size="sm">
                                <i className="bi bi-trash"></i>
                              </Button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </Card>
          </Tab>

          {/* ‚úÖ Add Room Tab */}
          <Tab eventKey="addroom" title="Add Room">
            <Card className="p-4 border-0 shadow-sm rounded-4 bg-white">
              <h5 className="fw-bold mb-4 text-center">Add New Room</h5>

              <Form onSubmit={handleSubmit}>
                {/* Room Name */}
                <Form.Group className="mb-3">
                  <Form.Label>Room Number</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="101"
                    required
                  />
                </Form.Group>

                {/* Room Type */}
                <Form.Group className="mb-3">
                  <Form.Label>Room Type</Form.Label>
                  <Form.Select
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Room Type</option>
                    <option>Standard</option>
                    <option>Deluxe</option>
                    <option>Suite</option>
                    <option>Family</option>
                    <option>Royal</option>
                  </Form.Select>
                </Form.Group>

                {/* Guests and Size */}
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Guests</Form.Label>
                      <Form.Control
                        type="number"
                        name="guests"
                        value={formData.guests}
                        onChange={handleChange}
                        placeholder="2"
                        required
                      />
                    </Form.Group>
                  </Col>

                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Size (sq ft)</Form.Label>
                      <Form.Control
                        type="number"
                        name="size"
                        value={formData.size}
                        onChange={handleChange}
                        placeholder="300"
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>

                {/* Price */}
                <Form.Group className="mb-3">
                  <Form.Label>Price per Night (‚Çπ)</Form.Label>
                  <Form.Control
                    type="number"
                    name="price"
                    value={formData.price}
                    onChange={handleChange}
                    placeholder="2999"
                    required
                  />
                </Form.Group>

                {/* Amenities */}
                <Form.Group className="mb-3">
                  <Form.Label>Amenities</Form.Label>
                  <div className="d-flex flex-wrap gap-3">
                    {[
                      "WiFi",
                      "TV",
                      "AC",
                      "Mini Bar",
                      "Breakfast",
                      "Sitting Area",
                      "Traditional Decor",
                      "Ganga View",
                      "Room Service",
                      "2 Bedrooms",
                      "Living Area",
                      "Butler Service",
                      "All Meals",
                      "Jacuzzi",
                    ].map((amenity, i) => (
                      <Form.Check
                        key={i}
                        type="checkbox"
                        label={amenity}
                        value={amenity}
                        checked={formData.amenities.includes(amenity)}
                        onChange={handleAmenityChange}
                        className="text-secondary"
                      />
                    ))}
                  </div>
                </Form.Group>

                {/* Description */}
                <Form.Group className="mb-3">
                  <Form.Label>Room Description</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={3}
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="Describe the room..."
                  />
                </Form.Group>

                {/* Room Image Upload */}
                <Form.Group className="mb-3">
                  <Form.Label>Upload Room Image</Form.Label>
                  <Form.Control
                    type="file"
                    name="image"
                    accept="image/*"
                    onChange={(e) =>
                      setFormData({ ...formData, image: e.target.files[0] })
                    }
                  />
                </Form.Group>

                {/* Submit Button */}
                <div className="text-center">
                  <Button
                    type="submit"
                    variant="warning"
                    className="px-5 text-white fw-semibold"
                    style={{ backgroundColor: "#f15a24", border: "none" }}
                  >
                    Add Room
                  </Button>
                </div>
              </Form>
            </Card>
          </Tab>
        </Tabs>

        <ToastContainer />
      </Container>
    </div>
  );
};

export default AdminDashboard;
