import React, { useState } from "react";
import { Container, Card, Form, Button } from "react-bootstrap";
import "bootstrap-icons/font/bootstrap-icons.css";
import { useNavigate } from "react-router-dom";
import "./AdminLogin.css";

const AdminLogin = () => {
  const [credentials, setCredentials] = useState({
    username: "",
    password: "",
  });

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    if (
      credentials.username === "admin" &&
      credentials.password === "admin123"
    ) {
      navigate("/admin/dashboard");
    }
  };

  return (
    <div className="admin-login-page d-flex align-items-center justify-content-center">
      <Container className="d-flex justify-content-center">
        <Card className="admin-card p-4 shadow-sm border rounded-4 text-center">
          <div className="mb-3">
            <i className="bi bi-lock-fill text-orange fs-1"></i>
          </div>
          <h5 className="fw-bold mb-1">Admin Login</h5>
          <p className="text-muted small mb-4">
            Enter your credentials to access the admin panel
          </p>

          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3 text-start">
              <Form.Label className="fw-semibold">Username</Form.Label>
              <Form.Control
                type="text"
                name="username"
                placeholder="admin"
                value={credentials.username}
                onChange={handleChange}
                className="rounded-3"
              />
            </Form.Group>

            <Form.Group className="mb-4 text-start">
              <Form.Label className="fw-semibold">Password</Form.Label>
              <Form.Control
                type="password"
                name="password"
                placeholder="********"
                value={credentials.password}
                onChange={handleChange}
                className="rounded-3"
              />
            </Form.Group>

            <Button
              type="submit"
              className="w-100 fw-semibold rounded-3 login-btn"
            >
              Login
            </Button>
          </Form>

          <p className="mt-3 text-muted small">
            Demo credentials: <strong>admin / admin123</strong>
          </p>
        </Card>
      </Container>
    </div>
  );
};

export default AdminLogin;
