import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  Modal,
  Toast,
  Spinner,
} from "react-bootstrap";
import "./Booking.css";

const Booking = () => {
  const location = useLocation();
  const roomId = location.state?.roomId;

  const [roomData, setRoomData] = useState(null);
  const [loadingRoom, setLoadingRoom] = useState(true);

  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    checkIn: "",
    checkOut: "",
    roomType: "",
    guests: "2 Guests",
    requests: "",
  });

  const [summary, setSummary] = useState({
    nights: 0,
    roomPrice: 0,
    roomCharges: 0,
    tax: 0,
    total: 0,
  });

  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showToast, setShowToast] = useState(false);

  const backendURL = "http://localhost:5000";

  // ‚úÖ Fetch room details dynamically
  useEffect(() => {
    if (!roomId) return;

    const fetchRoom = async () => {
      try {
        const res = await fetch(`${backendURL}/api/rooms`);
        const data = await res.json();
        if (data.success) {
          const room = data.data.find((r) => r.id === roomId);
          setRoomData(room);
          setFormData((prev) => ({
            ...prev,
            roomType: `${room.type} - ‚Çπ${room.price.toLocaleString()}/night`,
            guests: `${room.guests} Guests`,
          }));
        }
      } catch (err) {
        console.error("‚ùå Error fetching room:", err);
      } finally {
        setLoadingRoom(false);
      }
    };

    fetchRoom();
  }, [roomId]);

  // ‚úÖ Recalculate totals dynamically
  useEffect(() => {
    if (!roomData) return;

    const roomPrice = roomData.price || 0;
    const checkInDate = new Date(formData.checkIn);
    const checkOutDate = new Date(formData.checkOut);
    const diffTime = checkOutDate - checkInDate;
    const nights = diffTime > 0 ? diffTime / (1000 * 3600 * 24) : 0;
    const roomCharges = nights * roomPrice;
    const tax = roomCharges * 0.12;
    const total = roomCharges + tax;

    setSummary({ nights, roomPrice, roomCharges, tax, total });
  }, [formData.checkIn, formData.checkOut, roomData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const today = new Date().toISOString().split("T")[0];

  // ‚úÖ Payment and booking flow
  const handlePayment = async () => {
    if (
      !formData.fullName ||
      !formData.email ||
      !formData.checkIn ||
      !formData.checkOut
    ) {
      alert("Please fill all required fields before proceeding.");
      return;
    }

    try {
      const orderRes = await fetch(`${backendURL}/api/payment/create-order`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          amount: summary.total,
          receipt: "receipt_" + Date.now(),
        }),
      });

      const order = await orderRes.json();
      if (!order || !order.id) {
        alert("Unable to create order. Please try again.");
        return;
      }

      const options = {
        key: "rzp_test_Rcmx6BM6gABBYb",
        amount: order.amount,
        currency: order.currency,
        name: "Maheshwari Nivas",
        description: "Room Booking Payment",
        order_id: order.id,
        handler: async (response) => {
          const verifyRes = await fetch(`${backendURL}/api/payment/verify`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              ...response,
              bookingData: {
                ...formData,
                amount: summary.total,
                roomType: roomData.name,
              },
            }),
          });

          const verifyData = await verifyRes.json();

          if (verifyData.success) {
            setShowSuccessModal(true);
            setShowToast(true);

            setTimeout(() => {
              setShowSuccessModal(false);
              window.location.href = "/booking";
            }, 4000);
          } else {
            alert("Payment verification failed: " + verifyData.message);
          }
        },
        prefill: {
          name: formData.fullName,
          email: formData.email,
          contact: formData.phone,
        },
        theme: { color: "#E54C19" },
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (err) {
      console.error("‚ùå Payment error:", err);
      alert("Something went wrong with payment. Please try again.");
    }
  };

  return (
    <>
      <section className="booking-section py-5">
        <Container>
          <div className="text-center mb-5">
            <h2 className="fw-bold">Book Your Stay</h2>
            <p className="text-muted">
              Fill in the details below to reserve your room at Maheshwari Nivas
            </p>
          </div>

          {/* ‚úÖ Room Preview Section */}
          {loadingRoom ? (
            <div className="text-center py-5">
              <Spinner animation="border" variant="warning" />
            </div>
          ) : roomData ? (
            <Card className="shadow-sm border-0 rounded-4 mb-5 overflow-hidden">
              <Row className="g-0 align-items-center">
                <Col md={5}>
                  <img
                    src={roomData.image}
                    alt={roomData.name}
                    className="w-100 h-100 object-fit-cover"
                    style={{ minHeight: "250px", objectFit: "cover" }}
                  />
                </Col>
                <Col md={7} className="p-4">
                  <h4 className="fw-bold mb-2">{roomData.type}</h4>
                  <p className="text-muted mb-2">
                    {roomData.description || "Beautiful room for your stay."}
                  </p>
                  <p className="fw-semibold mb-1">
                    üë• {roomData.guests} Guests
                  </p>
                  <p className="fw-semibold text-orange">
                    ‚Çπ{roomData.price.toLocaleString()}/night
                  </p>
                </Col>
              </Row>
            </Card>
          ) : (
            <div className="text-center text-muted">Room not found.</div>
          )}

          {/* Booking Form + Summary */}
          <Row className="justify-content-center">
            <Col lg={8}>
              <Card className="shadow-sm border-0 rounded-4 p-4 mb-4">
                <h5 className="fw-bold mb-4">Booking Details</h5>
                <Form>
                  <Form.Group className="mb-3">
                    <Form.Label>Full Name *</Form.Label>
                    <Form.Control
                      type="text"
                      name="fullName"
                      placeholder="Enter your full name"
                      value={formData.fullName}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Row className="mb-3">
                    <Col md={6}>
                      <Form.Group controlId="email">
                        <Form.Label>Email *</Form.Label>
                        <Form.Control
                          type="email"
                          name="email"
                          placeholder="info@gmail.com"
                          value={formData.email}
                          onChange={handleChange}
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="phone">
                        <Form.Label>Phone *</Form.Label>
                        <Form.Control
                          type="tel"
                          name="phone"
                          placeholder="1234567890"
                          value={formData.phone}
                          onChange={handleChange}
                        />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row className="mb-3">
                    <Col md={6}>
                      <Form.Group controlId="checkIn">
                        <Form.Label>Check-in *</Form.Label>
                        <Form.Control
                          type="date"
                          name="checkIn"
                          min={today}
                          value={formData.checkIn}
                          onChange={handleChange}
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="checkOut">
                        <Form.Label>Check-out *</Form.Label>
                        <Form.Control
                          type="date"
                          name="checkOut"
                          min={formData.checkIn || today}
                          value={formData.checkOut}
                          onChange={handleChange}
                        />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Form.Group controlId="requests" className="mb-4">
                    <Form.Label>Special Requests</Form.Label>
                    <Form.Control
                      as="textarea"
                      rows={2}
                      name="requests"
                      value={formData.requests}
                      onChange={handleChange}
                      placeholder="Any special requests..."
                    />
                  </Form.Group>

                  <Button
                    onClick={handlePayment}
                    className="w-100 fw-semibold py-2 payment-btn"
                    disabled={!roomData}
                  >
                    Proceed to Payment
                  </Button>
                </Form>
              </Card>
            </Col>

            {/* Summary Card */}
            <Col lg={4}>
              <Card className="shadow-sm border-0 rounded-4 p-4 sticky-top summary-card">
                <h5 className="fw-bold mb-3">Booking Summary</h5>
                <p className="fw-semibold">
                  {roomData ? roomData.type : "Room"}
                </p>
                <hr />
                <div className="d-flex justify-content-between">
                  <span>Room Charges</span>
                  <span>‚Çπ{summary.roomCharges.toLocaleString()}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Taxes (12%)</span>
                  <span>‚Çπ{summary.tax.toLocaleString()}</span>
                </div>
                <div className="d-flex justify-content-between fw-bold text-orange border-top pt-2">
                  <span>Total</span>
                  <span>‚Çπ{summary.total.toLocaleString()}</span>
                </div>
              </Card>
            </Col>
          </Row>
        </Container>
      </section>

      {/* Success Modal */}
      <Modal show={showSuccessModal} centered>
        <div className="text-center p-4">
          <div
            className="rounded-circle bg-success text-white d-inline-flex align-items-center justify-content-center mb-3"
            style={{ width: "70px", height: "70px", fontSize: "32px" }}
          >
            ‚úì
          </div>
          <h4 className="fw-bold">Payment Successful!</h4>
          <p className="text-muted mb-1">
            Your booking has been confirmed successfully.
          </p>
        </div>
      </Modal>

      {/* Toast */}
      <Toast
        onClose={() => setShowToast(false)}
        show={showToast}
        delay={5000}
        autohide
        className="position-fixed bottom-0 end-0 m-4 bg-success text-white"
      >
        <Toast.Body>
          ‚úÖ Payment Successful! <br /> Confirmation mail sent to your email.
        </Toast.Body>
      </Toast>
    </>
  );
};

export default Booking;
