import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Tabs,
  Tab,
  Form,
  Modal,
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const navigate = useNavigate();

  const [rooms, setRooms] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    type: "",
    guests: "",
    size: "",
    price: "",
    amenities: [],
    description: "",
    status: "Available",
    current_guest: "",
    image: null,
  });
  const [showEditModal, setShowEditModal] = useState(false);
  const [editRoom, setEditRoom] = useState(null);

  // Fetch Rooms
  const fetchRooms = async () => {
    try {
      const res = await fetch("http://localhost:5050/api/rooms");
      const data = await res.json();
      if (data.success) setRooms(Array.isArray(data.data) ? data.data : []);
    } catch (err) {
      console.error("Error fetching rooms:", err);
    }
  };

  useEffect(() => {
    fetchRooms();
  }, []);

  // Logout
  const handleLogout = () => {
    toast.info("Logged out successfully", { autoClose: 1500 });
    setTimeout(() => navigate("/admin"), 1000);
  };

  // Input change
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setFormData({ ...formData, [name]: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  // Amenity selection
  const handleAmenityChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      amenities: checked
        ? [...prev.amenities, value]
        : prev.amenities.filter((a) => a !== value),
    }));
  };

  // Add Room
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.name || !formData.type || !formData.price) {
      toast.error("Please fill all required fields");
      return;
    }

    const roomData = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      if (key === "amenities") roomData.append(key, JSON.stringify(value));
      else roomData.append(key, value);
    });

    try {
      const res = await fetch("http://localhost:5050/api/rooms/add", {
        method: "POST",
        body: roomData,
      });
      const data = await res.json();
      if (data.success) {
        toast.success("✅ Room added successfully!");
        fetchRooms();
        setFormData({
          name: "",
          type: "",
          guests: "",
          size: "",
          price: "",
          amenities: [],
          description: "",
          image: null,
          status: "Available",
          current_guest: "",
        });
      } else {
        toast.error("Failed to add room");
      }
    } catch (err) {
      console.error(err);
      toast.error("Server error");
    }
  };

  // Delete Room
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this room?")) return;
    try {
      const res = await fetch(`http://localhost:5050/api/rooms/delete/${id}`, {
        method: "DELETE",
      });
      const data = await res.json();
      if (data.success) {
        toast.success("Room deleted");
        fetchRooms();
      } else {
        toast.error("Failed to delete");
      }
    } catch (err) {
      console.error(err);
      toast.error("Server error");
    }
  };

  // Open Edit Modal
  const openEditModal = (room) => {
    setEditRoom(room);
    setShowEditModal(true);
  };

  // Save Edit
  const handleEditSave = async () => {
    try {
      const res = await fetch(
        `http://localhost:5050/api/rooms/update/${editRoom.id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(editRoom),
        }
      );
      const data = await res.json();
      if (data.success) {
        toast.success("Room updated successfully");
        setShowEditModal(false);
        fetchRooms();
      } else toast.error("Update failed");
    } catch (err) {
      console.error(err);
      toast.error("Server error");
    }
  };

  return (
    <div className="admin-dashboard bg-light min-vh-100 py-4">
      <Container>
        {/* Header */}
        <div className="d-flex justify-content-between align-items-center mb-4">
          <div>
            <h3 className="fw-bold text-orange mb-1">Admin Panel</h3>
            <p className="text-muted small mb-0">Maheshwari Nivas Management</p>
          </div>
          <Button
            variant="outline-danger"
            className="rounded-3 px-3 fw-semibold"
            onClick={handleLogout}
          >
            <i className="bi bi-box-arrow-right me-2"></i> Logout
          </Button>
        </div>

        <Tabs
          defaultActiveKey="rooms"
          id="admin-tabs"
          className="mb-3 equal-tabs rounded-pill bg-white shadow-sm p-2"
        >
          {/* Room List */}
          <Tab eventKey="rooms" title="Rooms Status">
            <Card className="p-4 border-0 shadow-sm rounded-4 bg-white">
              <h5 className="fw-bold mb-4 text-center">Room Status</h5>
              {rooms.length === 0 ? (
                <p className="text-center text-muted">No rooms found</p>
              ) : (
                <div className="table-responsive">
                  <table className="table align-middle">
                    <thead className="table-light">
                      <tr>
                        <th>Room</th>
                        <th>Type</th>
                        <th>Status</th>
                        <th>Current Guest</th>
                        <th>Image</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {rooms.map((room) => (
                        <tr key={room.id}>
                          <td>{room.name}</td>
                          <td>{room.type}</td>
                          <td>
                            <span
                              className={`badge ${
                                room.status === "Available"
                                  ? "bg-success-subtle text-success"
                                  : "bg-warning-subtle text-warning"
                              }`}
                            >
                              {room.status}
                            </span>
                          </td>
                          <td>{room.current_guest || "-"}</td>
                          <td>
                            {room.image ? (
                              <img
                                src={`http://localhost:5050/${room.image}`}
                                alt="Room"
                                width="60"
                                className="rounded"
                              />
                            ) : (
                              "-"
                            )}
                          </td>
                          <td>
                            <Button
                              variant="outline-primary"
                              size="sm"
                              className="me-2"
                              onClick={() => openEditModal(room)}
                            >
                              Edit
                            </Button>
                            <Button
                              variant="outline-danger"
                              size="sm"
                              onClick={() => handleDelete(room.id)}
                            >
                              Delete
                            </Button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </Card>
          </Tab>

          {/* Add Room Tab */}
          <Tab eventKey="addroom" title="Add Room">
            <Card className="p-4 border-0 shadow-sm rounded-4 bg-white">
              <h5 className="fw-bold mb-4 text-center">Add New Room</h5>

              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                  <Form.Label>Room Number</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Room Type</Form.Label>
                  <Form.Select
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Type</option>
                    <option>Standard</option>
                    <option>Deluxe</option>
                    <option>Suite</option>
                    <option>Royal</option>
                  </Form.Select>
                </Form.Group>

                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Guests</Form.Label>
                      <Form.Control
                        type="number"
                        name="guests"
                        value={formData.guests}
                        onChange={handleChange}
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Size (sq ft)</Form.Label>
                      <Form.Control
                        type="number"
                        name="size"
                        value={formData.size}
                        onChange={handleChange}
                      />
                    </Form.Group>
                  </Col>
                </Row>

                <Form.Group className="mb-3">
                  <Form.Label>Price per Night (₹)</Form.Label>
                  <Form.Control
                    type="number"
                    name="price"
                    value={formData.price}
                    onChange={handleChange}
                  />
                </Form.Group>

                {/* Image Upload */}
                <Form.Group className="mb-3">
                  <Form.Label>Upload Room Image</Form.Label>
                  <Form.Control
                    type="file"
                    name="image"
                    accept="image/*"
                    onChange={handleChange}
                  />
                </Form.Group>

                {/* Submit */}
                <div className="text-center">
                  <Button
                    type="submit"
                    variant="warning"
                    className="px-5 text-white fw-semibold"
                    style={{ backgroundColor: "#f15a24", border: "none" }}
                  >
                    Add Room
                  </Button>
                </div>
              </Form>
            </Card>
          </Tab>
        </Tabs>

        <ToastContainer />

        {/* Edit Modal */}
        <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Edit Room</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            {editRoom && (
              <>
                <Form.Group className="mb-3">
                  <Form.Label>Room Name</Form.Label>
                  <Form.Control
                    type="text"
                    value={editRoom.name}
                    onChange={(e) =>
                      setEditRoom({ ...editRoom, name: e.target.value })
                    }
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Status</Form.Label>
                  <Form.Select
                    value={editRoom.status}
                    onChange={(e) =>
                      setEditRoom({ ...editRoom, status: e.target.value })
                    }
                  >
                    <option>Available</option>
                    <option>Occupied</option>
                    <option>Maintenance</option>
                  </Form.Select>
                </Form.Group>
              </>
            )}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowEditModal(false)}>
              Cancel
            </Button>
            <Button variant="primary" onClick={handleEditSave}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </Container>
    </div>
  );
};

export default AdminDashboard;
