import React from "react";
import { Card, Badge } from "react-bootstrap";

function RoomCard({ img, title, guests, size, desc, price, amenities }) {
  return (
    <Card className="shadow-sm border-0 rounded-4 overflow-hidden h-100 room-card">
      <div className="position-relative">
        <Card.Img variant="top" src={img} alt={title} />
        <Badge
          bg="light"
          text="dark"
          className="position-absolute top-0 end-0 m-2 fw-semibold px-3 py-2"
        >
          ‚Çπ{price}/night
        </Badge>
      </div>
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <p className="small text-muted mb-2">
          üë• {guests} Guests ‚Ä¢ üìè {size}
        </p>
        <p className="small">{desc}</p>
        <div className="mt-2">
          <strong>Amenities:</strong>
          <div className="d-flex flex-wrap gap-2 mt-1">
            {amenities.map((item, i) => (
              <span
                key={i}
                className="badge bg-light text-dark border fw-normal"
              >
                {item}
              </span>
            ))}
          </div>
        </div>
      </Card.Body>
    </Card>
  );
}

export default RoomCard;
