import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  Modal,
  Nav,
  Toast,
} from "react-bootstrap";
import "./Booking.css";

const Booking = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    checkIn: "",
    checkOut: "",
    roomType: "Standard Room - â‚¹1,999/night",
    guests: "2 Guests",
    requests: "",
  });

  const [summary, setSummary] = useState({
    nights: 0,
    roomPrice: 1999,
    roomCharges: 0,
    tax: 0,
    total: 0,
  });

  const [showModal, setShowModal] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState("Card");

  // New states for flow
  const [isProcessing, setIsProcessing] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showToast, setShowToast] = useState(false);

  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);

  useEffect(() => {
    const priceMap = {
      "Standard Room": 1999,
      "Deluxe Room": 2999,
      "Traditional Suite": 3499,
      "Premium Suite": 4999,
      "Family Room": 5999,
      "Royal Suite": 7999,
    };

    const roomTypeName = formData.roomType.split("-")[0].trim();
    const roomPrice = priceMap[roomTypeName] || 1999;

    const checkInDate = new Date(formData.checkIn);
    const checkOutDate = new Date(formData.checkOut);
    const diffTime = checkOutDate - checkInDate;
    const nights = diffTime > 0 ? diffTime / (1000 * 3600 * 24) : 0;

    const roomCharges = nights * roomPrice;
    const tax = roomCharges * 0.12;
    const total = roomCharges + tax;

    setSummary({ nights, roomPrice, roomCharges, tax, total });
  }, [formData.checkIn, formData.checkOut, formData.roomType]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setShowModal(true);
  };

  // ðŸ§© Payment process flow
  const handlePayment = () => {
    setIsProcessing(true);

    // Step 1: Show loader for 2 sec
    setTimeout(() => {
      setIsProcessing(false);
      setShowModal(false);
      setShowSuccessModal(true);

      // Step 2: Auto close modal after 10 sec
      setTimeout(() => {
        setShowSuccessModal(false);

        // Step 3: Show toast + reset form
        setShowToast(true);
        setFormData({
          fullName: "",
          email: "",
          phone: "",
          checkIn: "",
          checkOut: "",
          roomType: "Standard Room - â‚¹1,999/night",
          guests: "2 Guests",
          requests: "",
        });
      }, 10000);
    }, 2000);
  };

  const today = new Date().toISOString().split("T")[0];

  return (
    <>
      {/* ================= Booking Form ================= */}
      <section className="booking-section py-5">
        <Container>
          <div className="text-center mb-5">
            <h2 className="fw-bold">Book Your Stay</h2>
            <p className="text-muted">
              Fill in the details below to reserve your room at Maheshwari Nivas
            </p>
          </div>

          <Row className="justify-content-center">
            <Col lg={8}>
              <Card className="shadow-sm border-0 rounded-4 p-4 mb-4">
                <h5 className="fw-bold mb-4">Booking Details</h5>

                <Form onSubmit={handleSubmit}>
                  {/* Personal Info */}
                  <h6 className="fw-semibold mb-3">Personal Information</h6>
                  <Row className="mb-3">
                    <Col md={12}>
                      <Form.Group controlId="fullName">
                        <Form.Label>Full Name *</Form.Label>
                        <div className="input-group">
                          <span className="input-group-text">
                            <i className="fa-solid fa-user"></i>
                          </span>
                          <Form.Control
                            type="text"
                            name="fullName"
                            placeholder="Enter your full name"
                            value={formData.fullName}
                            onChange={handleChange}
                            required
                          />
                        </div>
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row className="mb-3">
                    <Col md={6}>
                      <Form.Group controlId="email">
                        <Form.Label>Email *</Form.Label>
                        <div className="input-group">
                          <span className="input-group-text">
                            <i className="fa-solid fa-envelope"></i>
                          </span>
                          <Form.Control
                            type="email"
                            name="email"
                            placeholder="info@gmail.com"
                            value={formData.email}
                            onChange={handleChange}
                            required
                          />
                        </div>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="phone">
                        <Form.Label>Phone Number *</Form.Label>
                        <div className="input-group">
                          <span className="input-group-text">
                            <i className="fa-solid fa-phone"></i>
                          </span>
                          <Form.Control
                            type="tel"
                            name="phone"
                            placeholder="1234567890"
                            value={formData.phone}
                            onChange={handleChange}
                            required
                          />
                        </div>
                      </Form.Group>
                    </Col>
                  </Row>

                  {/* Stay Info */}
                  <h6 className="fw-semibold mb-3 mt-4">Stay Details</h6>
                  <Row className="mb-3">
                    <Col md={6}>
                      <Form.Group controlId="checkIn">
                        <Form.Label>Check-in Date *</Form.Label>
                        <Form.Control
                          type="date"
                          name="checkIn"
                          min={today}
                          value={formData.checkIn}
                          onChange={handleChange}
                          required
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="checkOut">
                        <Form.Label>Check-out Date *</Form.Label>
                        <Form.Control
                          type="date"
                          name="checkOut"
                          min={formData.checkIn || today}
                          value={formData.checkOut}
                          onChange={handleChange}
                          required
                        />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row className="mb-3">
                    <Col md={6}>
                      <Form.Group controlId="roomType">
                        <Form.Label>Room Type *</Form.Label>
                        <Form.Select
                          name="roomType"
                          value={formData.roomType}
                          onChange={handleChange}
                        >
                          <option>Standard Room - â‚¹1,999/night</option>
                          <option>Deluxe Room - â‚¹2,999/night</option>
                          <option>Traditional Suite - â‚¹3,499/night</option>
                          <option>Premium Suite - â‚¹4,999/night</option>
                          <option>Family Room - â‚¹5,999/night</option>
                          <option>Royal Suite - â‚¹7,999/night</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="guests">
                        <Form.Label>Number of Guests *</Form.Label>
                        <Form.Select
                          name="guests"
                          value={formData.guests}
                          onChange={handleChange}
                        >
                          <option>1 Guest</option>
                          <option>2 Guests</option>
                          <option>3 Guests</option>
                          <option>4 Guests</option>
                          <option>5 Guests</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                  </Row>

                  <Form.Group controlId="requests" className="mb-4">
                    <Form.Label>Special Requests</Form.Label>
                    <Form.Control
                      as="textarea"
                      name="requests"
                      rows={2}
                      placeholder="Any special requirements or requests..."
                      value={formData.requests}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Button
                    type="submit"
                    className="w-100 fw-semibold py-2 payment-btn"
                  >
                    Proceed to Payment
                  </Button>
                </Form>
              </Card>
            </Col>

            {/* Summary Card */}
            <Col lg={4}>
              <Card className="shadow-sm border-0 rounded-4 p-4 sticky-top summary-card">
                <h5 className="fw-bold mb-3">Booking Summary</h5>
                <div className="d-flex justify-content-between fw-bold border-top pt-2">
                  <span>Total Amount</span>
                  <span>â‚¹{summary.total.toLocaleString()}</span>
                </div>
              </Card>
            </Col>
          </Row>
        </Container>
      </section>

      {/* Payment Modal */}
      <Modal show={showModal} onHide={handleClose} centered size="lg">
        <Modal.Header
          closeButton
          style={{
            background: "linear-gradient(90deg, #ff7b39, #e54c19)",
            color: "#fff",
          }}
        >
          <Modal.Title>Complete Payment</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h6>Total Amount</h6>
            <h4 className="text-orange fw-bold">
              â‚¹{summary.total.toLocaleString()}
            </h4>
          </div>

          <Nav
            variant="tabs"
            activeKey={paymentMethod}
            onSelect={(k) => setPaymentMethod(k)}
            className="mb-4 border-bottom-0"
          >
            <Nav.Item>
              <Nav.Link eventKey="Card">ðŸ’³ Card</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="UPI">ðŸ“± UPI</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="NetBanking">ðŸ¦ Net Banking</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Wallet">ðŸ’¼ Wallet</Nav.Link>
            </Nav.Item>
          </Nav>

          {/* Example: Card form */}
          {paymentMethod === "Card" && (
            <>
              <Form.Control className="mb-3" placeholder="Card Number" />
              <Form.Control className="mb-3" placeholder="Name on Card" />
            </>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button variant="outline-secondary" onClick={handleClose}>
            Cancel
          </Button>
          <Button
            disabled={isProcessing}
            className="payment-btn px-4"
            style={{
              background: "linear-gradient(90deg, #ff7b39, #e54c19)",
              border: "none",
            }}
            onClick={handlePayment}
          >
            {isProcessing ? (
              <>
                <span
                  className="spinner-border spinner-border-sm me-2"
                  role="status"
                ></span>
                Processing...
              </>
            ) : (
              <>Pay â‚¹{summary.total.toLocaleString()}</>
            )}
          </Button>
        </Modal.Footer>
      </Modal>

      {/* âœ… Success Modal */}
      <Modal show={showSuccessModal} centered>
        <div className="text-center p-4">
          <div
            className="rounded-circle bg-success text-white d-inline-flex align-items-center justify-content-center mb-3"
            style={{ width: "70px", height: "70px", fontSize: "32px" }}
          >
            âœ“
          </div>
          <h4 className="fw-bold">Payment Successful!</h4>
          <p className="text-muted mb-1">
            Your booking has been confirmed successfully.
          </p>
          <p className="fw-semibold">
            Booking ID: <span className="text-orange">MN{Date.now()}</span>
          </p>
        </div>
      </Modal>

      {/* âœ… Bottom-right Toast */}
      <div
        aria-live="polite"
        aria-atomic="true"
        className="position-fixed bottom-0 end-0 p-3"
        style={{ zIndex: 1060 }}
      >
        <Toast
          onClose={() => setShowToast(false)}
          show={showToast}
          delay={5000}
          autohide
          bg="success"
        >
          <Toast.Header closeButton={false}>
            <strong className="me-auto text-white">
              âœ… Payment Successful!
            </strong>
          </Toast.Header>
          <Toast.Body className="text-white">
            Your booking has been confirmed. A confirmation email has been sent.
          </Toast.Body>
        </Toast>
      </div>
    </>
  );
};

export default Booking;
