import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Tabs,
  Tab,
  Form,
  Table,
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import "./AdminDashboard.css";
import axios from "axios";

const AdminDashboard = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    type: "",
    guests: "",
    size: "",
    price: "",
    amenities: [],
    description: "",
    status: "Available",
    current_guest: "",
  });

  const [rooms, setRooms] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  // ✅ Fetch Rooms
  useEffect(() => {
    fetch("http://localhost:5000/api/rooms")
      .then((res) => res.json())
      .then((data) => {
        console.log("Rooms API response:", data);
        setRooms(Array.isArray(data.data) ? data.data : []);
      })
      .catch((err) => console.error("Error fetching rooms:", err));
  }, []);

  // ✅ Fetch Bookings
  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/bookings");
        if (res.data && Array.isArray(res.data.data)) {
          setBookings(res.data.data);
        } else {
          setBookings([]);
        }
      } catch (err) {
        console.error("Error fetching bookings:", err);
      }
    };
    fetchBookings();
  }, []);

  // ✅ Toast when admin logs in
  useEffect(() => {
    toast.success("Login Successful", {
      position: "bottom-right",
      autoClose: 3000,
      theme: "light",
    });
  }, []);

  // ✅ Logout
  const handleLogout = () => {
    toast.info("Logged out successfully", {
      position: "bottom-right",
      autoClose: 2000,
      theme: "light",
    });
    setTimeout(() => navigate("/admin"), 1000);
  };

  // ✅ Handle form changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ✅ Handle amenities
  const handleAmenityChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => {
      if (checked) return { ...prev, amenities: [...prev.amenities, value] };
      else
        return {
          ...prev,
          amenities: prev.amenities.filter((a) => a !== value),
        };
    });
  };

  // ✅ Submit new room
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/rooms/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await res.json();
      if (data.success) {
        toast.success("Room added successfully!", {
          position: "bottom-right",
          autoClose: 2000,
        });
        setFormData({
          name: "",
          type: "",
          guests: "",
          size: "",
          price: "",
          amenities: [],
          description: "",
        });
      } else {
        toast.error("❌ Failed to add room");
      }
    } catch (err) {
      console.error("Error:", err);
      toast.error("⚠️ Server error, please try again");
    }
  };

  return (
    <div className="admin-dashboard bg-light min-vh-100 py-4">
      <Container>
        {/* Header */}
        <div className="d-flex justify-content-between align-items-center mb-4">
          <div>
            <h3 className="fw-bold text-orange mb-1">Admin Panel</h3>
            <p className="text-muted small mb-0">Maheshwari Nivas Management</p>
          </div>

          <Button
            variant="outline-danger"
            className="rounded-3 px-3 fw-semibold"
            onClick={handleLogout}
          >
            <i className="bi bi-box-arrow-right me-2"></i> Logout
          </Button>
        </div>

        <hr />

        {/* Stats */}
        <Row className="g-4 mb-4">
          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-primary-subtle text-primary">
                  <i className="bi bi-calendar3"></i>
                </div>
                <div className="ms-3">
                  <h6>Total Bookings</h6>
                  <h5 className="fw-bold">{bookings.length}</h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-warning-subtle text-warning">
                  <i className="bi bi-house-door"></i>
                </div>
                <div className="ms-3">
                  <h6>Today's Bookings</h6>
                  <h5 className="fw-bold">
                    {
                      bookings.filter(
                        (b) =>
                          new Date(b.check_in).toDateString() ===
                          new Date().toDateString()
                      ).length
                    }
                  </h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-success-subtle text-success">
                  <i className="bi bi-person-check"></i>
                </div>
                <div className="ms-3">
                  <h6>Confirmed</h6>
                  <h5 className="fw-bold">
                    {bookings.filter((b) => b.status === "Confirmed").length}
                  </h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-purple-subtle text-purple">
                  <i className="bi bi-currency-dollar"></i>
                </div>
                <div className="ms-3">
                  <h6>Total Revenue</h6>
                  <h5 className="fw-bold">
                    ₹
                    {bookings
                      .reduce((sum, b) => sum + (b.amount || 0), 0)
                      .toLocaleString()}
                  </h5>
                </div>
              </div>
            </Card>
          </Col>
        </Row>

        {/* Tabs Section */}
        <Tabs
          defaultActiveKey="bookings"
          id="admin-tabs"
          className="mb-3 equal-tabs rounded-pill bg-white shadow-sm p-2"
        >
          {/* ✅ Bookings Tab */}
          <Tab eventKey="bookings" title="Bookings">
            <Card className="p-4 border-0 shadow-sm rounded-4 bg-white">
              <h5 className="fw-bold mb-4">All Bookings</h5>

              {bookings.length === 0 ? (
                <div className="text-center text-muted py-5">
                  <i className="bi bi-calendar2 fs-1 mb-3 d-block"></i>
                  <p>No bookings found</p>
                </div>
              ) : (
                <Table responsive bordered hover className="align-middle">
                  <thead className="table-light">
                    <tr>
                      <th>#</th>
                      <th>Guest Name</th>
                      <th>Room</th>
                      <th>Check-In</th>
                      <th>Check-Out</th>
                      <th>Status</th>
                      <th>Amount</th>
                    </tr>
                  </thead>
                  <tbody>
                    {bookings.map((b, i) => (
                      <tr key={b.id || i}>
                        <td>{i + 1}</td>
                        <td>{b.guest_name}</td>
                        <td>{b.room_number}</td>
                        <td>{new Date(b.check_in).toLocaleDateString()}</td>
                        <td>{new Date(b.check_out).toLocaleDateString()}</td>
                        <td>
                          <span
                            className={`badge ${
                              b.status === "Confirmed"
                                ? "bg-success-subtle text-success"
                                : b.status === "Pending"
                                ? "bg-warning-subtle text-warning"
                                : "bg-danger-subtle text-danger"
                            }`}
                          >
                            {b.status}
                          </span>
                        </td>
                        <td>₹{b.amount?.toLocaleString() || "0"}</td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              )}
            </Card>
          </Tab>

          {/* ✅ Rooms Tab */}
          {/* (same as before, not repeated for brevity) */}
        </Tabs>

        <ToastContainer />
      </Container>
    </div>
  );
};

export default AdminDashboard;
