import React, { useState } from "react";
import { Container, Button } from "react-bootstrap";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "./css/LocationSection.css";

function LocationSection() {
  const [showMap, setShowMap] = useState(false);

  return (
    <div className="py-5 text-center" style={{ backgroundColor: "#fffaf6" }}>
      <Container>
        <div
          className="d-flex flex-column align-items-center justify-content-center"
          style={{ margin: "0 auto" }}
        >
          <div
            className="mb-3"
            style={{
              fontSize: "2.5rem",
              color: "#F54900",
            }}
          >
            <i className="fa-solid fa-location-dot"></i>
          </div>

          <h4 className="fw-semibold mb-3">Find Us</h4>
          <p className="text-muted mb-4">
            Near <strong>Dashashwamedh Ghat, Varanasi</strong> <br />
            Visit us in the spiritual heart of the city.
          </p>

          {/* Get Directions Button */}
          <Button
            style={{
              backgroundColor: "#F54900",
              border: "none",
              borderRadius: "10px",
              padding: "10px 25px",
              fontWeight: "500",
            }}
            onClick={() => setShowMap(!showMap)}
          >
            <i className="fa-solid fa-map-location-dot me-2"></i>
            {showMap ? "Hide Map" : "Get Directions"}
          </Button>

          {/* Google Map iframe */}
          <div
            className={`map-container mt-4 ${
              showMap ? "map-show" : "map-hide"
            }`}
          >
            <iframe
              src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d115408.09799959231!2d82.90870810887782!3d25.32089491860156!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x398e2db76febcf4d%3A0x68131710853ff0b5!2sVaranasi%2C%20Uttar%20Pradesh!5e0!3m2!1sen!2sin!4v1761846739828!5m2!1sen!2sin"
              style={{ width: "100%", height: "450px", border: "0" }}
              width="600"
              height="450"
              style="border:0;"
              allowfullscreen=""
              loading="lazy"
              referrerpolicy="no-referrer-when-downgrade"
            ></iframe>
          </div>
        </div>
      </Container>
    </div>
  );
}

export default LocationSection;
