import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import RoomCard from "../components/RoomCard";

import room1 from "../assets/room1.jpg";
import room2 from "../assets/room2.jpg";
import room3 from "../assets/room3.jpg";

function Rooms() {
  const roomData = [
    {
      img: room1,
      title: "Standard Room",
      guests: 2,
      size: "250 sq ft",
      desc: "Cozy room perfect for couples with essential amenities and comfortable bedding.",
      price: 1999,
      amenities: ["WiFi", "TV", "AC", "Breakfast"],
    },
    {
      img: room2,
      title: "Deluxe Room",
      guests: 3,
      size: "350 sq ft",
      desc: "Spacious room with modern amenities and elegant interiors for a comfortable stay.",
      price: 2999,
      amenities: ["WiFi", "TV", "AC", "Mini Bar", "Breakfast"],
    },
    {
      img: room3,
      title: "Traditional Suite",
      guests: 3,
      size: "400 sq ft",
      desc: "Experience authentic Banarasi hospitality with traditional decor and modern comforts.",
      price: 3499,
      amenities: ["WiFi", "TV", "AC", "Sitting Area"],
    },
  ];

  return (
    <>
      <section
        className="text-center py-5 text-white"
        style={{ backgroundColor: "#FF501C" }}
      >
        <Container>
          <h2 className="fw-bold mb-2">Our Rooms & Suites</h2>
          <p className="mb-0">
            Choose from our range of comfortable and luxurious accommodations
          </p>
        </Container>
      </section>

      <Container className="my-5">
        <Row className="gy-4">
          {roomData.map((room, index) => (
            <Col md={4} key={index}>
              <RoomCard {...room} />
            </Col>
          ))}
        </Row>
      </Container>
    </>
  );
}

export default Rooms;
