import React, { useEffect, useState } from "react";
import { Container, Row, Col, Card, Button, Spinner } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Rooms.css";

const fallbackRooms = [
  {
    img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822641/deluxeRoom_ysyd2x.jpg",
    title: "Standard Room",
    guests: 2,
    size: "250 sq ft",
    desc: "Cozy room perfect for couples with essential amenities and comfortable bedding.",
    price: 1999,
    amenities: ["WiFi", "TV", "AC", "Breakfast"],
  },
  {
    img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822642/premiumRoom_uupmcg.jpg",
    title: "Deluxe Room",
    guests: 3,
    size: "350 sq ft",
    desc: "Spacious room with modern amenities and elegant interiors for a comfortable stay.",
    price: 2999,
    amenities: ["WiFi", "TV", "AC", "Mini Bar", "Breakfast"],
  },
  {
    img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822642/traditionalRoom_sdbglh.jpg",
    title: "Traditional Suite",
    guests: 3,
    size: "400 sq ft",
    desc: "Experience authentic Banarasi hospitality with traditional decor and modern comforts.",
    price: 3499,
    amenities: [
      "WiFi",
      "TV",
      "AC",
      "Traditional Decor",
      "Sitting Area",
      "Breakfast",
    ],
  },
  {
    img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822641/deluxeRoom_ysyd2x.jpg",
    title: "Premium Suite",
    guests: 4,
    size: "500 sq ft",
    desc: "Luxurious suite with breathtaking views of the Ganges and premium amenities.",
    price: 4999,
    amenities: [
      "WiFi",
      "TV",
      "AC",
      "Gangas View",
      "Mini Bar",
      "Breakfast",
      "Room Service",
    ],
  },
  {
    img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822642/premiumRoom_uupmcg.jpg",
    title: "Family Room",
    guests: 5,
    size: "600 sq ft",
    desc: "Perfect for families with separate bedrooms and spacious living area.",
    price: 5999,
    amenities: [
      "WiFi",
      "TV",
      "AC",
      "2 Bedrooms",
      "Living Area",
      "Breakfast",
      "Room Service",
    ],
  },
  {
    img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822642/traditionalRoom_sdbglh.jpg",
    title: "Royal Suite",
    guests: 4,
    size: "800 sq ft",
    desc: "Our most luxurious offering with exclusive amenities and unparalleled service.",
    price: 7999,
    amenities: ["WiFi", "TV", "AC", "Jacuzzi", "Butler Service", "All Meals"],
  },
];

// icon mapper (keeps your previous icons)
const getAmenityIcon = (amenity) => {
  if (!amenity)
    return <i className="fa-solid fa-circle-check me-1 text-orange"></i>;
  switch (String(amenity).toLowerCase()) {
    case "wifi":
      return <i className="fa-solid fa-wifi me-1 text-orange"></i>;
    case "tv":
      return <i className="fa-solid fa-tv me-1 text-orange"></i>;
    case "ac":
      return <i className="fa-regular fa-snowflake me-1 text-orange"></i>;
    case "breakfast":
      return <i className="fa-solid fa-mug-saucer me-1 text-orange"></i>;
    case "mini bar":
      return <i className="fa-solid fa-wine-glass me-1 text-orange"></i>;
    case "sitting area":
      return <i className="fa-solid fa-couch me-1 text-orange"></i>;
    default:
      return <i className="fa-solid fa-circle-check me-1 text-orange"></i>;
  }
};

const Rooms = () => {
  const navigate = useNavigate();
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);

  // Map backend room object to UI fields:
  // expected backend fields: image, price, type/name/title, guests, size, description, amenities
  const mapBackendRoom = (r) => {
    return {
      img: r.image || r.img || r.photo || "",
      title: r.type || r.name || r.title || "Room",
      guests: r.guests || r.capacity || 2,
      size: r.size || r.area || "‚Äî",
      desc: r.description || r.desc || "",
      price: r.price || r.rate || 0,
      amenities: Array.isArray(r.amenities)
        ? r.amenities
        : (() => {
            try {
              return r.amenities ? JSON.parse(r.amenities) : [];
            } catch {
              return [];
            }
          })(),
      // include raw backend object (helpful if you want to pass to booking page)
      raw: r,
    };
  };

  useEffect(() => {
    let mounted = true;
    const fetchRooms = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/rooms");
        // API returns { success: true, data: [...] } in your controller
        const data = res.data && (res.data.data || res.data);
        if (Array.isArray(data) && mounted) {
          setRooms(data.map(mapBackendRoom));
        } else {
          // fallback to static if response shape unexpected
          setRooms(fallbackRooms);
          console.warn(
            "Rooms: unexpected API response, using fallback.",
            res.data
          );
        }
      } catch (err) {
        console.error("Error fetching rooms, using fallback:", err);
        setRooms(fallbackRooms);
      } finally {
        if (mounted) setLoading(false);
      }
    };

    fetchRooms();
    return () => {
      mounted = false;
    };
  }, []);

  // redirect to booking page with room data
  const handleBooking = (room) => {
    navigate("/booking", { state: { room } });
  };

  return (
    <section>
      {/* Header Section */}
      <div
        className="text-center py-5 text-white"
        style={{ backgroundColor: "#E54C19" }}
      >
        <h2 className="fw-bold">Our Rooms & Suites</h2>
        <p>
          Choose from our range of comfortable and luxurious accommodations.
        </p>
      </div>

      {/* Rooms Section */}
      <Container className="my-5">
        {loading ? (
          <div className="text-center py-5">
            <Spinner animation="border" variant="warning" />
          </div>
        ) : (
          <Row className="g-4">
            {rooms.map((room, index) => (
              <Col md={4} key={index}>
                <Card className="shadow-sm h-100 bm border-0 rounded-4 overflow-hidden room-card">
                  <div className="position-relative mb-3">
                    <Card.Img
                      variant="top"
                      src={room.img}
                      alt={room.title}
                      loading="lazy"
                      style={{ height: "220px", objectFit: "cover" }}
                      className="room-image"
                    />
                    {/* Price Tag */}
                    <div
                      className="position-absolute top-0 end-0 d-flex align-items-center rounded-2 price-tag"
                      style={{
                        padding: "4px 10px",
                        margin: "8px",
                      }}
                    >
                      <span
                        style={{
                          color: "#E54C19",
                          fontWeight: "600",
                          marginRight: "4px",
                        }}
                      >
                        ‚Çπ{room.price}
                      </span>
                      <span style={{ color: "#666", fontSize: "0.85rem" }}>
                        /night
                      </span>
                    </div>
                  </div>

                  <Card.Body>
                    <Card.Title className="mb-3">{room.title}</Card.Title>
                    <p className="text-muted mb-3">
                      üë• {room.guests} Guests &nbsp; | &nbsp; üìê {room.size}
                    </p>
                    <Card.Text className="small mb-3">{room.desc}</Card.Text>

                    <p className="fw-semibold mb-2">Amenities:</p>
                    <div className="d-flex flex-wrap gap-2 mb-3">
                      {(room.amenities && room.amenities.length > 0
                        ? room.amenities
                        : []
                      ).map((a, i) => (
                        <span
                          key={i}
                          className="badge bg-light text-dark border border-secondary"
                        >
                          {getAmenityIcon(a)}
                          {a}
                        </span>
                      ))}
                    </div>

                    <Button
                      onClick={() => handleBooking(room.raw || room)}
                      variant="dark"
                      className="w-100 fw-semibold book-btn"
                      style={{ backgroundColor: "#E54C19", border: "none" }}
                    >
                      Book This Room
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </Container>
    </section>
  );
};

export default Rooms;
