import React from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";

const Rooms = () => {
  const roomData = [
    {
      img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822641/deluxeRoom_ysyd2x.jpg",
      title: "Standard Room",
      guests: 2,
      size: "250 sq ft",
      desc: "Cozy room perfect for couples with essential amenities and comfortable bedding.",
      price: 1999,
      amenities: ["WiFi", "TV", "AC", "Breakfast"],
    },
    {
      img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822642/premiumRoom_uupmcg.jpg",
      title: "Deluxe Room",
      guests: 3,
      size: "350 sq ft",
      desc: "Spacious room with modern amenities and elegant interiors for a comfortable stay.",
      price: 2999,
      amenities: ["WiFi", "TV", "AC", "Mini Bar", "Breakfast"],
    },
    {
      img: "https://res.cloudinary.com/dynx8bauw/image/upload/v1761822642/traditionalRoom_sdbglh.jpg",
      title: "Traditional Suite",
      guests: 3,
      size: "400 sq ft",
      desc: "Experience authentic Banarasi hospitality with traditional decor and modern comforts.",
      price: 3499,
      amenities: ["WiFi", "TV", "AC", "Sitting Area"],
    },
  ];

  return (
    <section>
      {/* Header Section */}
      <div
        className="text-center py-5 text-white"
        style={{ backgroundColor: "#E54C19" }}
      >
        <h2 className="fw-bold">Our Rooms & Suites</h2>
        <p>
          Choose from our range of comfortable and luxurious accommodations.
        </p>
      </div>

      {/* Rooms Section */}
      <Container className="my-5">
        <Row className="g-4">
          {roomData.map((room, index) => (
            <Col md={4} key={index} className="rounded-5">
              <Card className="shadow-sm h-100 border-0">
                <div className="position-relative">
                  <Card.Img
                    variant="top"
                    src={room.img}
                    alt={room.title}
                    loading="lazy"
                    style={{ height: "220px", objectFit: "cover" }}
                  />
                  <div
                    className="position-absolute top-0 end-0 bg-white px-2 py-1 fw-bold rounded-bottom-start"
                    style={{ fontSize: "0.9rem" }}
                  >
                    <span style={{ color: "#F98900" }}>‚Çπ{room.price}</span>
                    <span>/night</span>
                  </div>
                </div>
                <Card.Body>
                  <Card.Title>{room.title}</Card.Title>
                  <p className="text-muted mb-1">
                    üë• {room.guests} Guests &nbsp; | &nbsp; üìê {room.size}
                  </p>
                  <Card.Text className="small mb-3">{room.desc}</Card.Text>
                  <p className="fw-semibold mb-2">Amenities:</p>
                  <div className="d-flex flex-wrap gap-2 mb-3">
                    {room.amenities.map((a, i) => (
                      <span
                        key={i}
                        className="badge bg-light text-dark border border-secondary"
                      >
                        {a}
                      </span>
                    ))}
                  </div>
                  <Button
                    variant="dark"
                    className="w-100 fw-semibold"
                    style={{ backgroundColor: "#E54C19", border: "none" }}
                  >
                    Book Now
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
};

export default Rooms;
