import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Tabs,
  Tab,
  Form,
  Table,
  Spinner,
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import "./AdminDashboard.css";
import axios from "axios";

const AdminDashboard = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    type: "",
    guests: "",
    size: "",
    price: "",
    amenities: [],
    description: "",
    status: "Available",
    current_guest: "",
  });

  const [rooms, setRooms] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loadingBookings, setLoadingBookings] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  // Fetch Rooms
  useEffect(() => {
    fetch("http://localhost:5000/api/rooms")
      .then((res) => res.json())
      .then((data) => {
        console.log("Rooms API response:", data);
        setRooms(Array.isArray(data.data) ? data.data : []);
      })
      .catch((err) => console.error("Error fetching rooms:", err));
  }, []);

  // Fetch Bookings
  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/bookings");
        console.log("Bookings API response:", res.data);
        setBookings(res.data.data || []);
      } catch (err) {
        console.error("Error fetching bookings:", err);
        toast.error("âš ï¸ Failed to fetch bookings!");
      } finally {
        setLoadingBookings(false);
      }
    };
    fetchBookings();
  }, []);

  useEffect(() => {
    toast.success("Login Successful", {
      position: "bottom-right",
      autoClose: 3000,
      theme: "light",
    });
  }, []);

  // ðŸ§  Handle logout
  const handleLogout = () => {
    toast.info("Logged out successfully", {
      position: "bottom-right",
      autoClose: 2000,
      theme: "light",
    });
    setTimeout(() => navigate("/admin"), 1000);
  };

  // ðŸ§  Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // ðŸ§  Handle checkbox (amenities)
  const handleAmenityChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => {
      if (checked) return { ...prev, amenities: [...prev.amenities, value] };
      else
        return {
          ...prev,
          amenities: prev.amenities.filter((a) => a !== value),
        };
    });
  };

  return (
    <div className="admin-dashboard bg-light min-vh-100 py-4">
      <Container>
        {/* Header */}
        <div className="d-flex justify-content-between align-items-center mb-4">
          <div>
            <h3 className="fw-bold text-orange mb-1">Admin Panel</h3>
            <p className="text-muted small mb-0">Maheshwari Nivas Management</p>
          </div>

          <Button
            variant="outline-danger"
            className="rounded-3 px-3 fw-semibold"
            onClick={handleLogout}
          >
            <i className="bi bi-box-arrow-right me-2"></i> Logout
          </Button>
        </div>

        <hr />

        {/* Stats Section */}
        <Row className="g-4 mb-4">
          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-primary-subtle text-primary">
                  <i className="bi bi-calendar3"></i>
                </div>
                <div className="ms-3">
                  <h6>Total Bookings</h6>
                  <h5 className="fw-bold">{bookings.length}</h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-warning-subtle text-warning">
                  <i className="bi bi-house-door"></i>
                </div>
                <div className="ms-3">
                  <h6>Today's Bookings</h6>
                  <h5 className="fw-bold">
                    {
                      bookings.filter((b) => {
                        const today = new Date().toISOString().split("T")[0];
                        return b.checkIn?.split("T")[0] === today;
                      }).length
                    }
                  </h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-success-subtle text-success">
                  <i className="bi bi-person-check"></i>
                </div>
                <div className="ms-3">
                  <h6>Confirmed</h6>
                  <h5 className="fw-bold">
                    {bookings.filter((b) => b.status === "Confirmed").length}
                  </h5>
                </div>
              </div>
            </Card>
          </Col>

          <Col md={3}>
            <Card className="stat-card p-3 border-0 shadow-sm rounded-4">
              <div className="d-flex align-items-center">
                <div className="icon-box bg-purple-subtle text-purple">
                  <i className="bi bi-currency-dollar"></i>
                </div>
                <div className="ms-3">
                  <h6>Total Revenue</h6>
                  <h5 className="fw-bold">
                    â‚¹
                    {bookings
                      .reduce((sum, b) => sum + (Number(b.totalAmount) || 0), 0)
                      .toLocaleString()}
                  </h5>
                </div>
              </div>
            </Card>
          </Col>
        </Row>

        {/* Tabs Section */}
        <Tabs
          defaultActiveKey="bookings"
          id="admin-tabs"
          className="mb-3 equal-tabs rounded-pill bg-white shadow-sm p-2"
        >
          {/* âœ… Bookings Tab */}
          <Tab eventKey="bookings" title="Bookings">
            <Card className="p-4 border-0 shadow-sm rounded-4 bg-white">
              <h5 className="fw-bold mb-4">All Bookings</h5>

              {loadingBookings ? (
                <div className="text-center py-5">
                  <Spinner animation="border" variant="warning" />
                </div>
              ) : bookings.length === 0 ? (
                <p className="text-center text-muted mb-0">
                  No bookings found yet.
                </p>
              ) : (
                <div className="table-responsive">
                  <Table striped bordered hover>
                    <thead className="table-light">
                      <tr>
                        <th>#</th>
                        <th>Guest Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Room</th>
                        <th>Check-In</th>
                        <th>Check-Out</th>
                        <th>Amount</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {bookings.map((b, index) => (
                        <tr key={b._id}>
                          <td>{index + 1}</td>
                          <td>{b.name}</td>
                          <td>{b.email}</td>
                          <td>{b.phone}</td>
                          <td>{b.room}</td>
                          <td>{b.checkIn?.split("T")[0]}</td>
                          <td>{b.checkOut?.split("T")[0]}</td>
                          <td>â‚¹{b.totalAmount}</td>
                          <td>
                            <span
                              className={`badge ${
                                b.status === "Confirmed"
                                  ? "bg-success-subtle text-success"
                                  : b.status === "Pending"
                                  ? "bg-warning-subtle text-warning"
                                  : "bg-danger-subtle text-danger"
                              }`}
                            >
                              {b.status}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </div>
              )}
            </Card>
          </Tab>

          {/* Rooms Tab and Add Room remain same */}
          {/* ... existing Rooms Status and Add Room code ... */}
        </Tabs>

        <ToastContainer />
      </Container>
    </div>
  );
};

export default AdminDashboard;
