import React from "react";
import { Button, Container } from "react-bootstrap";
import "./HeroSection.css";

function HeroSection() {
  return (
    <div className="hero-section d-flex align-items-center justify-content-center text-center text-white">
      <Container>
        <h5 className="fw-bold mb-2">महेश्वरी निवास</h5>
        <h1 className="mb-3">Maheshwari Nivas</h1>
        <p className="lead mb-4">
          Experience the divine essence of Banaras with world-class hospitality
        </p>
        <div>
          <Button variant="danger" className="me-3 px-4 py-2 fw-semibold">
            <i className="bi bi-calendar-check me-2"></i>Book Now
          </Button>
          <Button variant="light" className="px-4 py-2 fw-semibold">
            Explore Rooms
          </Button>
        </div>
      </Container>
    </div>
  );
}

export default HeroSection;
