import React from "react";
import { Container, Button } from "react-bootstrap";
import "@fortawesome/fontawesome-free/css/all.min.css";

function LocationSection() {
  return (
    <div className="py-5 text-center" style={{ backgroundColor: "#fffaf6" }}>
      <Container>
        <div
          className="d-flex flex-column align-items-center justify-content-center"
          style={{ maxWidth: "700px", margin: "0 auto" }}
        >
          <div
            className="mb-3"
            style={{
              fontSize: "2.5rem",
              color: "#F54900",
            }}
          >
            <i className="fa-solid fa-location-dot"></i>
          </div>

          <h4 className="fw-semibold mb-3">Prime Location</h4>
          <p className="text-muted mb-4">
            Situated near <strong>Dashashwamedh Ghat</strong>, we are at the
            heart of Varanasiâ€™s spiritual and cultural activities. Enjoy easy
            access to temples, ghats, and local markets.
          </p>

          <Button
            style={{
              backgroundColor: "#F54900",
              border: "none",
              borderRadius: "10px",
              padding: "10px 25px",
              fontWeight: "500",
            }}
            onClick={() =>
              window.open(
                "https://www.google.com/maps/place/Dashashwamedh+Ghat,+Varanasi",
                "_blank"
              )
            }
          >
            <i className="fa-solid fa-map-location-dot me-2"></i>
            Get Directions
          </Button>
        </div>
      </Container>
    </div>
  );
}

export default LocationSection;
