import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  Modal,
  Nav,
  Toast,
} from "react-bootstrap";
import "./Booking.css";

const Booking = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    checkIn: "",
    checkOut: "",
    roomType: "Standard Room - ₹1,999/night",
    guests: "2 Guests",
    requests: "",
  });

  const [summary, setSummary] = useState({
    nights: 0,
    roomPrice: 1999,
    roomCharges: 0,
    tax: 0,
    total: 0,
  });

  const [showModal, setShowModal] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState("Card");
  const [processing, setProcessing] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showToast, setShowToast] = useState(false);

  const handleClose = () => setShowModal(false);

  // ✅ Calculate price summary dynamically
  useEffect(() => {
    const priceMap = {
      "Standard Room": 1999,
      "Deluxe Room": 2999,
      "Traditional Suite": 3499,
      "Premium Suite": 4999,
      "Family Room": 5999,
      "Royal Suite": 7999,
    };

    const roomTypeName = formData.roomType.split("-")[0].trim();
    const roomPrice = priceMap[roomTypeName] || 1999;

    const checkInDate = new Date(formData.checkIn);
    const checkOutDate = new Date(formData.checkOut);
    const diffTime = checkOutDate - checkInDate;
    const nights = diffTime > 0 ? diffTime / (1000 * 3600 * 24) : 0;

    const roomCharges = nights * roomPrice;
    const tax = roomCharges * 0.12;
    const total = roomCharges + tax;

    setSummary({ nights, roomPrice, roomCharges, tax, total });
  }, [formData.checkIn, formData.checkOut, formData.roomType]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setShowModal(true);
  };

  const today = new Date().toISOString().split("T")[0];

  // ✅ Handle Payment & Save Booking
  const handlePayment = async () => {
    setProcessing(true);

    try {
      // Send booking details to backend API
      const response = await fetch("http://localhost:5000/api/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...formData,
          totalAmount: summary.total,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        console.log("✅ Booking saved:", result);

        // Step 1: Close Payment Modal
        setProcessing(false);
        setShowModal(false);

        // Step 2: Show Success Modal
        setShowSuccessModal(true);

        // Step 3: Auto-close Success Modal and show Toast
        setTimeout(() => {
          setShowSuccessModal(false);
          setShowToast(true);

          // Reset form
          setFormData({
            fullName: "",
            email: "",
            phone: "",
            checkIn: "",
            checkOut: "",
            roomType: "Standard Room - ₹1,999/night",
            guests: "2 Guests",
            requests: "",
          });
        }, 10000);
      } else {
        alert("❌ Booking failed. Please try again.");
        setProcessing(false);
      }
    } catch (err) {
      console.error("❌ Error submitting booking:", err);
      alert("Server error. Please check backend connection.");
      setProcessing(false);
    }
  };

  return (
    <>
      {/* ================= Booking Form ================= */}
      <section className="booking-section py-5">
        <Container>
          <div className="text-center mb-5">
            <h2 className="fw-bold">Book Your Stay</h2>
            <p className="text-muted">
              Fill in the details below to reserve your room at Maheshwari Nivas
            </p>
          </div>

          <Row className="justify-content-center">
            {/* Left: Booking Form */}
            <Col lg={8}>
              <Card className="shadow-sm border-0 rounded-4 p-4 mb-4">
                <h5 className="fw-bold mb-4">Booking Details</h5>

                <Form onSubmit={handleSubmit}>
                  {/* Personal Info */}
                  <h6 className="fw-semibold mb-3">Personal Information</h6>
                  <Row className="mb-3">
                    <Col md={12}>
                      <Form.Group controlId="fullName">
                        <Form.Label>Full Name *</Form.Label>
                        <div className="input-group">
                          <span className="input-group-text">
                            <i className="fa-solid fa-user"></i>
                          </span>
                          <Form.Control
                            type="text"
                            name="fullName"
                            placeholder="Enter your full name"
                            value={formData.fullName}
                            onChange={handleChange}
                            required
                          />
                        </div>
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row className="mb-3">
                    <Col md={6}>
                      <Form.Group controlId="email">
                        <Form.Label>Email *</Form.Label>
                        <div className="input-group">
                          <span className="input-group-text">
                            <i className="fa-solid fa-envelope"></i>
                          </span>
                          <Form.Control
                            type="email"
                            name="email"
                            placeholder="info@gmail.com"
                            value={formData.email}
                            onChange={handleChange}
                            required
                          />
                        </div>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="phone">
                        <Form.Label>Phone Number *</Form.Label>
                        <div className="input-group">
                          <span className="input-group-text">
                            <i className="fa-solid fa-phone"></i>
                          </span>
                          <Form.Control
                            type="tel"
                            name="phone"
                            placeholder="1234567890"
                            value={formData.phone}
                            onChange={handleChange}
                            required
                          />
                        </div>
                      </Form.Group>
                    </Col>
                  </Row>

                  {/* Stay Info */}
                  <h6 className="fw-semibold mb-3 mt-4">Stay Details</h6>
                  <Row className="mb-3">
                    <Col md={6}>
                      <Form.Group controlId="checkIn">
                        <Form.Label>Check-in Date *</Form.Label>
                        <Form.Control
                          type="date"
                          name="checkIn"
                          min={today}
                          value={formData.checkIn}
                          onChange={handleChange}
                          required
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="checkOut">
                        <Form.Label>Check-out Date *</Form.Label>
                        <Form.Control
                          type="date"
                          name="checkOut"
                          min={formData.checkIn || today}
                          value={formData.checkOut}
                          onChange={handleChange}
                          required
                        />
                      </Form.Group>
                    </Col>
                  </Row>

                  <Row className="mb-3">
                    <Col md={6}>
                      <Form.Group controlId="roomType">
                        <Form.Label>Room Type *</Form.Label>
                        <Form.Select
                          name="roomType"
                          value={formData.roomType}
                          onChange={handleChange}
                        >
                          <option>Standard Room - ₹1,999/night</option>
                          <option>Deluxe Room - ₹2,999/night</option>
                          <option>Traditional Suite - ₹3,499/night</option>
                          <option>Premium Suite - ₹4,999/night</option>
                          <option>Family Room - ₹5,999/night</option>
                          <option>Royal Suite - ₹7,999/night</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="guests">
                        <Form.Label>Number of Guests *</Form.Label>
                        <Form.Select
                          name="guests"
                          value={formData.guests}
                          onChange={handleChange}
                        >
                          <option>1 Guest</option>
                          <option>2 Guests</option>
                          <option>3 Guests</option>
                          <option>4 Guests</option>
                          <option>5 Guests</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                  </Row>

                  <Form.Group controlId="requests" className="mb-4">
                    <Form.Label>Special Requests</Form.Label>
                    <Form.Control
                      as="textarea"
                      name="requests"
                      rows={2}
                      placeholder="Any special requirements or requests..."
                      value={formData.requests}
                      onChange={handleChange}
                    />
                  </Form.Group>

                  <Button
                    type="submit"
                    className="w-100 fw-semibold py-2 payment-btn"
                  >
                    Proceed to Payment
                  </Button>
                </Form>
              </Card>
            </Col>

            {/* Right: Booking Summary */}
            <Col lg={4}>
              <Card className="shadow-sm border-0 rounded-4 p-4 sticky-top summary-card">
                <h5 className="fw-bold mb-3">Booking Summary</h5>
                <p className="mb-1 text-muted small">Room Type</p>
                <p className="fw-semibold">{formData.roomType.split("-")[0]}</p>

                <p className="mb-1 text-muted small">Duration</p>
                <p className="fw-semibold">
                  {formData.checkIn && formData.checkOut
                    ? `${new Date(
                        formData.checkIn
                      ).toLocaleDateString()} - ${new Date(
                        formData.checkOut
                      ).toLocaleDateString()}`
                    : "—"}
                  {summary.nights > 0 && (
                    <>
                      {" "}
                      ({summary.nights} night
                      {summary.nights > 1 ? "s" : ""})
                    </>
                  )}
                </p>

                <p className="mb-1 text-muted small">Guests</p>
                <p className="fw-semibold">{formData.guests}</p>

                <hr />
                <div className="d-flex justify-content-between">
                  <span>Room Charges</span>
                  <span>₹{summary.roomCharges.toLocaleString()}</span>
                </div>
                <div className="d-flex justify-content-between mb-2">
                  <span>Taxes & Fees (12%)</span>
                  <span>₹{summary.tax.toLocaleString()}</span>
                </div>

                <div className="d-flex justify-content-between fw-bold text-orange border-top pt-2">
                  <span>Total Amount</span>
                  <span>₹{summary.total.toLocaleString()}</span>
                </div>

                <div
                  className="p-3 rounded-3 mt-3"
                  style={{ backgroundColor: "#FFF5EF" }}
                >
                  <ul className="list-unstyled mb-0 small">
                    <li>✓ Free cancellation up to 24 hours before check-in</li>
                    <li>✓ Complimentary breakfast included</li>
                    <li>
                      ✓ Free WiFi <i className="fa-solid fa-wifi"></i> & parking
                    </li>
                  </ul>
                </div>
              </Card>
            </Col>
          </Row>
        </Container>
      </section>

      {/* ================= Payment Modal ================= */}
      <Modal show={showModal} onHide={handleClose} centered size="lg">
        <Modal.Header
          closeButton
          style={{
            background: "linear-gradient(90deg, #ff7b39, #e54c19)",
            color: "#fff",
          }}
        >
          <Modal.Title className="fw-semibold">Complete Payment</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h6 className="mb-0">Total Amount</h6>
            <h4 className="text-orange fw-bold mb-0">
              ₹{summary.total.toLocaleString()}
            </h4>
          </div>

          {/* Payment Tabs */}
          <Nav
            variant="tabs"
            activeKey={paymentMethod}
            onSelect={(k) => setPaymentMethod(k)}
            className="mb-4 border-bottom-0"
          >
            <Nav.Item>
              <Nav.Link eventKey="Card">💳 Card</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="UPI">📱 UPI</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="NetBanking">🏦 Net Banking</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="Wallet">💼 Wallet</Nav.Link>
            </Nav.Item>
          </Nav>

          {paymentMethod === "Card" && (
            <div className="card-payment-section">
              <Form.Group className="mb-3">
                <Form.Label>Card Number</Form.Label>
                <Form.Control type="text" placeholder="1234 5678 9012 3456" />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Cardholder Name</Form.Label>
                <Form.Control type="text" placeholder="Name on Card" />
              </Form.Group>
              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Expiry Date</Form.Label>
                    <Form.Control type="text" placeholder="MM/YY" />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>CVV</Form.Label>
                    <Form.Control type="password" placeholder="123" />
                  </Form.Group>
                </Col>
              </Row>
              <p className="small text-muted mb-0">
                🔐 Your payment is secured with 256-bit SSL encryption.
              </p>
            </div>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button variant="outline-secondary cancel-btn" onClick={handleClose}>
            Cancel
          </Button>
          <Button
            className="payment-btn"
            disabled={processing}
            onClick={handlePayment}
          >
            {processing ? (
              <>
                <span className="spinner-border spinner-border-sm me-2"></span>
                Processing...
              </>
            ) : (
              `Pay ₹${summary.total.toLocaleString()}`
            )}
          </Button>
        </Modal.Footer>
      </Modal>

      {/* ✅ Success Modal */}
      <Modal show={showSuccessModal} centered>
        <div className="text-center p-4">
          <div
            className="rounded-circle bg-success text-white d-inline-flex align-items-center justify-content-center mb-3"
            style={{ width: "70px", height: "70px", fontSize: "32px" }}
          >
            ✓
          </div>
          <h4 className="fw-bold">Payment Successful!</h4>
          <p className="text-muted mb-1">
            Your booking has been confirmed successfully.
          </p>
          <p className="fw-semibold">
            Booking ID: <span className="text-orange">MN{Date.now()}</span>
          </p>
        </div>
      </Modal>

      {/* ✅ Bottom-right Toast */}
      <Toast
        onClose={() => setShowToast(false)}
        show={showToast}
        delay={5000}
        autohide
        className="position-fixed bottom-0 end-0 m-4 bg-success text-white"
      >
        <Toast.Body>
          <div>
            <p>Payment Successful!</p>
            <i className="fa-solid fa-circle-check"></i> Your booking has been
            confirmed. Confirmation email sent.
          </div>
        </Toast.Body>
      </Toast>
    </>
  );
};

export default Booking;
