import React, { useState } from "react";
import { Container, Button } from "react-bootstrap";
import GalleryHero from "../components/GalleryHero";
import GalleryFilters from "../components/GalleryFilters";
import GalleryGrid from "../components/GalleryGrid";
import "../styles/Gallery.css";

function GalleryPage() {
  const categories = [
    "All",
    "Location",
    "Interior",
    "Rooms",
    "Restaurant",
    "Amenities",
  ];

  const allImages = [
    {
      url: "https://res.cloudinary.com/demo/image/upload/sample.jpg",
      category: "Location",
    },
    {
      url: "https://res.cloudinary.com/demo/image/upload/v1690975673/hotel_room.jpg",
      category: "Rooms",
    },
    {
      url: "https://res.cloudinary.com/demo/image/upload/v1690975673/restaurant.jpg",
      category: "Restaurant",
    },
    {
      url: "https://res.cloudinary.com/demo/image/upload/v1690975673/lobby.jpg",
      category: "Interior",
    },
    {
      url: "https://res.cloudinary.com/demo/image/upload/v1690975673/pool.jpg",
      category: "Amenities",
    },
  ];

  const [filtered, setFiltered] = useState(allImages);

  const handleFilterChange = (category) => {
    if (category === "All") {
      setFiltered(allImages);
    } else {
      setFiltered(allImages.filter((img) => img.category === category));
    }
  };

  return (
    <>
      {/* ✅ Hero Section */}
      <GalleryHero />

      {/* ✅ Filters */}
      <GalleryFilters
        categories={categories}
        onFilterChange={handleFilterChange}
      />

      {/* ✅ Image Grid */}
      <GalleryGrid images={filtered} />

      {/* ✅ Experience the Magic Section */}
      <Container className="my-5">
        <div className="p-5 bg-white shadow-sm rounded-4 text-center experience-card">
          <h4 className="fw-bold mb-3">Experience the Magic</h4>
          <p className="text-muted mb-3">
            Our gallery showcases the perfect blend of traditional Banarasi
            architecture and modern luxury. From our elegantly designed rooms to
            the spiritual ambiance of nearby ghats, every moment at Maheshwari
            Nivas is picture-perfect.
          </p>
          <p className="text-muted mb-0">
            Book your stay today and create your own unforgettable memories in
            the heart of Varanasi.
          </p>
        </div>
      </Container>

      {/* ✅ Instagram Follow Section */}
      <Container className="text-center my-5">
        <h5 className="fw-bold mb-3">Follow Us on Instagram</h5>
        <p className="text-muted mb-4">
          Share your experience with <strong>#MaheshwariNivas</strong> and get
          featured on our page
        </p>
        <Button
          className="instagram-btn px-4 py-2 rounded-pill border-0"
          href="https://instagram.com/maheshwarinivas"
          target="_blank"
        >
          <i className="bi bi-instagram me-2"></i>@maheshwarinivas
        </Button>
      </Container>
    </>
  );
}

export default GalleryPage;
